import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import APIDataService from "../services/apis";
import Pagination from "https://cdn.skypack.dev/rc-pagination@3.1.15";

function ApiList(props) {
	const [apis, setApis] = useState([]);
	const [searchName, setSearchName] = useState("");
	const [searchCategory, setSearchCategory] = useState("");
	const [searchCuisine, setSearchCuisine] = useState("");
	const [page, setPage] = useState(0);

	const [perPage, setPerPage] = useState(10);
	const [size, setSize] = useState(perPage);
	const [current, setCurrent] = useState(1);

	const PerPageChange = (value) => {
		setSize(value);
		const newPerPage = Math.ceil(apis.length / value);
		if (current > newPerPage) {
			setCurrent(newPerPage);
		}
	};

	const getData = (current, pageSize) => {
		// Normally you should get the data from the server
		return apis.slice((current - 1) * pageSize, current * pageSize);
	};

	const PaginationChange = (page, pageSize) => {
		setCurrent(page);
		setSize(pageSize);
	};

	const PrevNextArrow = (current, type, originalElement) => {
		if (type === "prev") {
			return (
				<button>
					<i className="fa fa-angle-double-left"></i>
				</button>
			);
		}
		if (type === "next") {
			return (
				<button>
					<i className="fa fa-angle-double-right"></i>
				</button>
			);
		}
		return originalElement;
	};

	// const [cuisines, setCuisines] = useState(["All Cuisines"]);

	useEffect(() => {
		retrieveAPIs();
	}, []);

	const onChangeSearchName = (e) => {
		const searchName = e.target.value;
		setSearchName(searchName);
	};

	const onChangeSearchCategory = (e) => {
		const searchCategory = e.target.value;
		setSearchCategory(searchCategory);
	};

	// const onChangeSearchCuisine = (e) => {
	// 	const searchCuisine = e.target.value;
	// 	setSearchCuisine(searchCuisine);
	// };

	const retrieveAPIs = () => {
		APIDataService.getAll()
			.then((response) => {
				console.log(response.data);
				setApis(response.data.apis);
				setTotal(response.data.total_results / 20);
			})
			.catch((e) => {
				console.log(e);
			});
	};

	// const retrieveCuisines = () => {
	// 	ApiDataService.getCuisines()
	// 		.then((response) => {
	// 			console.log(response.data);
	// 			setCuisines(["All Cuisines"].concat(response.data));
	// 		})
	// 		.catch((e) => {
	// 			console.log(e);
	// 		});
	// };

	const refreshList = () => {
		retrieveAPIs();
	};

	const find = (query, by) => {
		APIDataService.find(query, by)
			.then((response) => {
				console.log(query);
				console.log(response.data);
				setApis(response.data.apis);
				setPage(0);
			})
			.catch((e) => {
				console.log(e);
			});
	};
	const changePage = (page) => {
		APIDataService.getAll(page)
			.then((response) => {
				console.log(response.data);
				setApis(response.data.apis);
			})
			.catch((e) => {
				console.log(e);
			});
	};
	const findByName = () => {
		find(searchName, "API");
	};

	const findByCategory = () => {
		find(searchCategory, "Category");
	};

	const findByPage = () => {
		changePage(page);
	};

	// const findByCuisine = () => {
	// 	if (searchCuisine == "All Cuisines") {
	// 		refreshList();
	// 	} else {
	// 		find(searchCuisine, "cuisine");
	// 	}
	// };

	return (
		<div>
			<div className="row pb-1">
				<div className="input-group col-lg-4">
					<input
						type="text"
						className="form-control"
						placeholder="Search by Name"
						value={searchName}
						onChange={onChangeSearchName}
					/>
					<div className="input-group-append">
						<button
							className="btn btn-outline-secondary"
							type="button"
							onClick={findByName}
						>
							Search
						</button>
					</div>
				</div>
				<div className="input-group col-lg-4">
					<input
						type="text"
						className="form-control"
						placeholder="Search by Category"
						value={searchCategory}
						onChange={onChangeSearchCategory}
					/>
					<div className="input-group-append">
						<button
							className="btn btn-outline-secondary"
							type="button"
							onClick={findByCategory}
						>
							Search
						</button>
					</div>
				</div>
				{/* <div className="input-group col-lg-4">
					<select onChange={onChangeSearchCuisine}>
						{cuisines.map((cuisine) => {
							return <option value={cuisine}> {cuisine.substr(0, 20)} </option>;
						})}
					</select>
					<div className="input-group-append">
						<button
							className="btn btn-outline-secondary"
							type="button"
							onClick={findByCuisine}
						>
							Search
						</button>
					</div> 
				</div>*/}
				<Pagination
					className="pagination-data"
					showTotal={(total, range) =>
						`Showing ${range[0]}-${range[1]} of ${total}`
					}
					onChange={PaginationChange}
					total={apis.length}
					current={current}
					pageSize={size}
					showSizeChanger={false}
					itemRender={PrevNextArrow}
					onShowSizeChange={PerPageChange}
				/>
				<button
					onClick={() => {
						setPage(page + 1);
						findByPage();
					}}
				>
					+1
				</button>
				<div>{page}</div>
				<button
					onClick={() => {
						setPage(page - 1);
						findByPage();
					}}
				>
					-1
				</button>
			</div>
			<div className="row">
				{apis.map((api) => {
					return (
						<div className="col-lg-4 pb-1" key={api.id}>
							<div className="card">
								<div className="card-body">
									<h5 className="card-title">{api.API}</h5>
									<p className="card-text">
										<strong>Author: </strong>
										{api.Auth ? (
											api.Auth
										) : (
											<span
												style={{ fontStyle: "italic" }}
												className="font-weight-bold"
											>
												unknown
											</span>
										)}
										<br />
										<strong>Description: </strong>
										{api.Description}
										<br />
										<strong>Category: </strong>
										{api.Category}
										<br />
										<strong>Link: </strong>
										{api.Link}
										<br />
									</p>
								</div>
							</div>
						</div>
					);
				})}
			</div>
		</div>
	);
}

export default ApiList;
