import http from "../http-common";

class APIDataService {
	getAll(page = 3) {
		return http.get(`?page=${page}`);
	}

	find(query, by = "name", page = 0) {
		return http.get(`?${by}=${query}&page=${page}`);
	}

	// getCuisines(id) {
	// 	return http.get(`/cuisines`);
	// }
}

export default new APIDataService();
