import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import APIDataService from "../services/apis";
import Pagination from "https://cdn.skypack.dev/rc-pagination@3.1.15";

function ApiList(props) {
	const [apis, setApis] = useState([]);
	const [searchName, setSearchName] = useState("");
	const [searchCategory, setSearchCategory] = useState("");
	// const [searchCuisine, setSearchCuisine] = useState("");
	const [page, setPage] = useState(0);
	const [total, setTotal] = useState(0);
	const [perPage, setPerPage] = useState(20);
	const [size, setSize] = useState(perPage);
	const [current, setCurrent] = useState(1);

	const PerPageChange = (value) => {
		setSize(value);
		const newPerPage = Math.ceil(total / value);
		if (current > newPerPage) {
			setCurrent(newPerPage);
		}
	};

	const PaginationChange = (page, pageSize) => {
		setCurrent(page);
		setSize(pageSize);
		changePage(page - 1);
	};

	const PrevNextArrow = (current, type, originalElement) => {
		if (type === "prev") {
			return (
				<button>
					<i className="fa fa-angle-double-left"></i>
				</button>
			);
		}
		if (type === "next") {
			return (
				<button>
					<i className="fa fa-angle-double-right"></i>
				</button>
			);
		}
		return originalElement;
	};

	// const [cuisines, setCuisines] = useState(["All Cuisines"]);

	useEffect(() => {
		retrieveAPIs();
	}, []);

	const onChangeSearchName = (e) => {
		const searchName = e.target.value;
		setSearchName(searchName);
	};

	const onChangeSearchCategory = (e) => {
		const searchCategory = e.target.value;
		setSearchCategory(searchCategory);
	};

	// const onChangeSearchCuisine = (e) => {
	// 	const searchCuisine = e.target.value;
	// 	setSearchCuisine(searchCuisine);
	// };

	const retrieveAPIs = () => {
		APIDataService.getAll()
			.then((response) => {
				console.log(response.data);
				setApis(response.data.apis);
				setTotal(response.data.total_results);
				setCurrent(1);
			})
			.catch((e) => {
				console.log(e);
			});
	};

	// const retrieveCuisines = () => {
	// 	ApiDataService.getCuisines()
	// 		.then((response) => {
	// 			console.log(response.data);
	// 			setCuisines(["All Cuisines"].concat(response.data));
	// 		})
	// 		.catch((e) => {
	// 			console.log(e);
	// 		});
	// };

	const refreshList = () => {
		retrieveAPIs();
	};

	const find = (query, by) => {
		APIDataService.find(query, by)
			.then((response) => {
				console.log(query);
				console.log(response.data);
				setApis(response.data.apis);
				setPage(0);
				setTotal(response.data.total_results);
				setCurrent(1);
			})
			.catch((e) => {
				console.log(e);
			});
	};
	const changePage = (page) => {
		APIDataService.getAll(page)
			.then((response) => {
				console.log(response.data);
				setApis(response.data.apis);
			})
			.catch((e) => {
				console.log(e);
			});
	};

	const findByName = () => {
		find(searchName, "API");
	};

	const findByCategory = () => {
		find(searchCategory, "Category");
	};

	// const findByCuisine = () => {
	// 	if (searchCuisine == "All Cuisines") {
	// 		refreshList();
	// 	} else {
	// 		find(searchCuisine, "cuisine");
	// 	}
	// };

	return (
		<div className="row pb-1">
			<div className="input-group col-lg-4">
				<input
					type="text"
					className="form-control"
					placeholder="Search by Name"
					value={searchName}
					onChange={onChangeSearchName}
				/>
				<div className="input-group-append">
					<button
						className="btn btn-outline-secondary"
						type="button"
						onClick={findByName}
					>
						Search
					</button>
				</div>
			</div>
			<div className="input-group col-lg-4">
				<input
					type="text"
					className="form-control"
					placeholder="Search by Category"
					value={searchCategory}
					onChange={onChangeSearchCategory}
				/>
				<div className="input-group-append">
					<button
						className="btn btn-outline-secondary"
						type="button"
						onClick={findByCategory}
					>
						Search
					</button>
				</div>
			</div>
			<div className="input-group col-lg-4">
				<button
					className="btn btn-outline-secondary"
					type="button"
					onClick={refreshList}
				>
					Refresh
				</button>
			</div>

			<div className="container-fluid mt-5 mb-5">
				<div className="row justify-content-center">
					<div className="col-md-10">
						<div className="card">
							<div className="card-body p-0">
								<div className="table-filter-info">
									<Pagination
										className="pagination-data"
										showTotal={(total, range) =>
											`Showing ${range[0]}-${range[1]} of ${total}`
										}
										onChange={PaginationChange}
										total={total}
										current={current}
										pageSize={size}
										showSizeChanger={false}
										itemRender={PrevNextArrow}
										onShowSizeChange={PerPageChange}
									/>
								</div>
								<div className="card" style={{ width: "18rem" }}>
									{apis.map((data, index) => {
										return (
											<div className="card-body" key={data.id}>
												<h5 className="card-title">{data.API}</h5>
												<h6 className="card-subtitle mb-2 text-muted">
													{data.Auth ? (
														data.Auth
													) : (
														<span className="font-italic text-muted">
															unknown
														</span>
													)}
												</h6>
												<p className="card-text">
													Some quick example text to build on the card title and
													make up the bulk of the card's content.
												</p>
												<a href="#" className="card-link">
													Card link
												</a>
												<a href="#" className="card-link">
													Another link
												</a>
											</div>
										);
									})}
								</div>
								<div className="table-responsive">
									<table className="table table-text-small mb-0">
										<tbody>
											{apis.map((data, index) => {
												return (
													<tr key={data.id}>
														<td>{data.API}</td>
														<td>{data.Description}</td>
														<td>
															{data.Auth ? (
																data.Auth
															) : (
																<span className="font-italic text-muted">
																	unknown
																</span>
															)}
														</td>
														<td>{data.Category}</td>
														<td>
															<a href={`${data.Link}`} target="_blank">
																{data.Link}
															</a>
														</td>
													</tr>
												);
											})}
										</tbody>
									</table>
								</div>
								<div className="table-filter-info">
									<Pagination
										className="pagination-data"
										showTotal={(total, range) =>
											`Showing ${range[0]}-${range[1]} of ${total}`
										}
										onChange={PaginationChange}
										total={total}
										current={current}
										pageSize={size}
										showSizeChanger={false}
										itemRender={PrevNextArrow}
										onShowSizeChange={PerPageChange}
									/>
								</div>
							</div>
						</div>
					</div>
				</div>
			</div>
		</div>
	);
}

export default ApiList;
